mapper.select:
SELECT p.PRODUCT_ID ,p.PRODUCT_CODE ,p.PRODUCT_NAME ,p.PRODUCT_CONTENT ,p.COST_PRICE ,p.PRODUCT_PRICE ,o.OPTION_ID ,o.OPTION_NAME ,o.OPTION_VALUE ,o.ADDITIONAL_PRICE, CASE WHEN p.SALED_PRICE IS NULL THEN (p.PRODUCT_PRICE + IFNULL(o.ADDITIONAL_PRICE,0)) ELSE p.SALED_PRICE END AS TOTAL_PRICE ,mc.MAIN_CATE_NM ,sc.SUB_CATE_NM ,p.IS_VISIBLE, IFNULL(i.QUANTITY, 0) AS QUANTITY, CASE WHEN IFNULL(i.QUANTITY, 0) = 0 THEN 'SOLDOUT' ELSE '' END AS SOLD_OUT, p.INPUT_DT ,p.UPDATE_DT FROM PRODUCTS p LEFT JOIN OPTIONS o ON p.PRODUCT_ID = o.PRODUCT_ID LEFT JOIN INVENTORIES i ON o.OPTION_ID = i.OPTION_ID JOIN SUB_CATEGORIES sc ON p.SUB_CATE_ID = sc.SUB_CATE_ID JOIN MAIN_CATEGORIES mc ON sc.MAIN_CATE_ID = mc.MAIN_CATE_ID WHERE 1=1
AND p.PRODUCT_CODE LIKE CONCAT('%', #{SEARCH_TEXT}, '%')
AND p.PRODUCT_NAME LIKE CONCAT('%', #{SEARCH_TEXT}, '%')
AND p.IS_VISIBLE = #{IS_VISIBLE}
AND IFNULL(i.QUANTITY, 0) > 0
AND IFNULL(i.QUANTITY, 0) = 0
AND DATE(COALESCE(p.UPDATE_DT, p.INPUT_DT)) >= #{START_DATE}
AND DATE(COALESCE(p.UPDATE_DT, p.INPUT_DT)) <= #{END_DATE}
AND mc.MAIN_CATE_ID = #{MAIN_CATE_ID}
AND sc.SUB_CATE_ID = #{SUB_CATE_ID}
ORDER BY p.PRODUCT_ID, o.OPTION_ID

mapper.update:
UPDATE product_img
SET IS_MAIN = 'Y'
WHERE PRODUCT_ID = #{PRODUCT_ID}
AND IMAGE_ID = (
SELECT t.IMAGE_ID FROM (
SELECT IMAGE_ID
FROM product_img
WHERE PRODUCT_ID = #{PRODUCT_ID}
ORDER BY IMAGE_ID ASC
LIMIT 1
) t
)

UPDATE OPTIONS
   SET OPTION_NAME = #{OPTION_NAME}
	  ,OPTION_VALUE = #{OPTION_VALUE}
	  ,ADDITIONAL_PRICE = #{ADDITIONAL_PRICE}
	  ,UPDATE_DT = NOW()
	  ,UPDATE_ID = #{UPDATE_ID}
 WHERE OPTION_ID = #{OPTION_ID}
