mapper.update:
UPDATE CART_ITEMS CI
 INNER JOIN CARTS C
	ON CI.CART_ID = C.CART_ID
   SET CI.QUANTITY = #{quantity}
	 , CI.SUB_TOTAL = #{subTotal}
 WHERE CI.CART_ID = #{cartId}
   AND CI.CART_ITEM_ID = #{cartItemId}
   AND CI.PRODUCT_OPTION = #{option}

UPDATE CART_ITEMS
   SET IS_CHECKED = #{isChecked}
 WHERE 1=1
 	AND CART_ID = #{cartId}

		AND CART_ITEM_ID = #{cartItemId}

mapper.delete:
DELETE FROM CART_ITEMS
 WHERE CART_ID = #{cartId}

		AND CART_ITEM_ID = #{cartItemId}


		AND IS_CHECKED = 'Y'

mapper.update:
UPDATE CART_ITEMS
	SET PRODUCT_OPTION = #{option}
	WHERE CART_ID = #{cartId}
		AND CART_ITEM_ID = #{cartItemId}

UPDATE CART_ITEMS
	SET SUB_TOTAL = #{subTotal}
	WHERE CART_ITEM_ID = #{cartItemId}

mapper.delete:
DELETE WI 
FROM WISH_ITEMS WI
INNER JOIN WISH W ON WI.WISH_ID = W.WISH_ID
WHERE W.MEMBER_ID = #{memberId} 
AND WI.PRODUCT_ID = #{productId}

mapper.select:
SELECT WISH_ID, MEMBER_ID
FROM WISH
WHERE MEMBER_ID = #{memberId}
