mapper.select:
SELECT MEMBER_ID, LOGIN_TYPE
FROM MEMBERS
WHERE EMAIL_ADDR = #{to}

SELECT MEMBER_ID, "O" as PASSWORD
FROM   MEMBERS
WHERE  MEMBER_ID = #{id}
  AND  PASSWORD = #{password}

SELECT COUNT(MEMBER_ID) AS COUNT
FROM MEMBERS
WHERE MEMBER_ID = #{id}

SELECT COUNT(MEMBER_ID) AS COUNT
FROM MEMBERS
WHERE EMAIL_ADDR = #{to}

SELECT IFNULL(SUM(`POINT`), 0) AS total
FROM POINTS
WHERE MEMBER_ID = #{MEMBER_ID}

SELECT IFNULL(SUM(FINAL_AMOUNT),0) AS total
FROM ORDERS
WHERE MEMBER_ID = #{MEMBER_ID}

mapper.update:
UPDATE MEMBERS
SET LAST_LOGIN_DT = NOW()
WHERE MEMBER_ID = #{id}

UPDATE MEMBERS
SET PASSWORD = #{password}
WHERE MEMBER_ID = #{id}

UPDATE MEMBERS
   SET STATUS = 'N'
   WHERE MEMBER_ID = #{id}
